// Contact Form Plugin
// -----------------------------------
jQuery(document).ready(function(){
  
  $('#message').hide();

  // Add validation parts
  $('#contact input[type=text], #contact input[type=number], #contact input[type=email], #contact select, #contact textarea').each(function(){
    $(this).after('<mark class="validate"></mark>');
  });

  // Validate as you type
  $('#name, #comments, #subject').focusout(function() {
    if (!$(this).val())
      $(this).addClass('error').parent().find('mark').removeClass('valid').addClass('error');
    else
      $(this).removeClass('error').parent().find('mark').removeClass('error').addClass('valid');
  });
  $('#email').focusout(function() {
    if (!$(this).val() || !isEmail($(this).val()))
      $(this).addClass('error').parent().find('mark').removeClass('valid').addClass('error');
    else
      $(this).removeClass('error').parent().find('mark').removeClass('error').addClass('valid');
  });
  $('#verify').focusout(function() {
    var verify = $(this).val();
    var verify_box = $(this);
    if (!verify)
      $(this).addClass('error').parent().find('mark').removeClass('valid').addClass('error');
    else {

      // Test verification code via ajax
      $.ajax({
        type: 'POST',
        url: 'verify/ajax_check.php',
        data: { verify: verify },
        async: false,
        success: function( data ) {
          if (data=='success') {
            $(verify_box).removeClass('error').parent().find('mark').removeClass('error').addClass('valid');
          } else {
            $(verify_box).addClass('error').parent().find('mark').removeClass('valid').addClass('error');
          }
        }
      });

    }
  });

  $('#submit').click(function() {
    $("#message").slideUp(200,function() {
      $('#message').hide();

      // Kick in Validation
      $('#name, #subject, #comments, #verify, #email').triggerHandler("focusout");

      if ($('#contact mark.error').size()>0) {
        if(shake == "Yes") {
          $('#contact').effect('shake', { times:2 }, 75, function(){
            $('#contact input.error:first, #contact textarea.error:first').focus();
          });
        } else $('#contact input.error:first, #contact textarea.error:first').focus();

        return false;
      }

    });
  });

  $('#contactform').submit(function(){

    if ($('#contact mark.error').size()>0) {
      if(shake == "Yes") {
      $('#contact').effect('shake', { times:2 }, 75);
      }
      return false;
    }

    var action = $(this).attr('action');

    $.post(action, {
      name: $('#name').val(),
      email: $('#email').val(),
      subject: $('#subject').val(),
      comments: $('#comments').val(),
      verify: $('#verify').val()
    },
      function(data){
        $('#message').html( data );
        $('#message').slideDown();
        $('#contactform img.loader').fadeOut('slow',function(){$(this).remove()});
        $('#contactform #submit').attr('disabled','');
        if(data.match('success') != null) $('#contactform')[0].reset();

      }
    );

    return false;

  });

  function isEmail(emailAddress) {
    var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
    return pattern.test(emailAddress);
  }

  function isNumeric(input) {
    return (input - 0) == input && input.length > 0;
  }

});


// ScrollTo Plugin
// -----------------------------------
/*!
 * jquery.scrollto.js 0.0.1 - https://github.com/yckart/jquery.scrollto.js
 * Scroll smooth to any element in your DOM.
 *
 * Copyright (c) 2012 Yannick Albert (http://yckart.com)
 * Licensed under the MIT license (http://www.opensource.org/licenses/mit-license.php).
 * 2013/02/17
 **/
$.scrollTo = $.fn.scrollTo = function(x, y, options){
    
  if (!(this instanceof $)) return $.fn.scrollTo.apply($('html, body'), arguments);

  options = $.extend({}, {
    gap: {
      x: 0,
      y: 0
    },
    animation: {
      easing: 'swing',
      duration: 600,
      complete: $.noop,
      step: $.noop
    }
  }, options);

  return this.each(function(){
    var elem = $(this);
    elem.stop().animate({
      scrollLeft: !isNaN(Number(x)) ? x : $(y).offset().left + options.gap.x,
      scrollTop: !isNaN(Number(y)) ? y : $(y).offset().top + options.gap.y
    }, options.animation);
  });

};

$(document).ready(function(){
  /**
   * This part causes smooth scrolling using scrollto.js
   * We target all a tags inside the nav, and apply the scrollto.js to it.
   */
  $("body a").not(".noteasing").click(function(evn){
      evn.preventDefault();
      $('html,body').scrollTo(this.hash, this.hash); 
  });
    
 /**
   * This part handles the highlighting functionality.
   * We use the scroll functionality again, some array creation and 
   * manipulation, class adding and class removing, and conditional testing
   */
  var aChildren = $("navbar li").children(); // find the a children of the list items
  var aArray = []; // create the empty aArray
  for (var i=0; i < aChildren.length; i++) {    
    var aChild = aChildren[i];
    var ahref = $(aChild).attr('href');
    aArray.push(ahref);
  } // this for loop fills the aArray with attribute href values
  
  $(window).scroll(function(){
    var windowPos = $(window).scrollTop(); // get the offset of the window from the top of page
    var windowHeight = $(window).height(); // get the height of the window
    var docHeight = $(document).height();
    
    for (var i=0; i < aArray.length; i++) {
      var theID = aArray[i];
      console.log(theID);
      var divPos = $(theID).offset().top; // get the offset of the div from the top of page
      console.log('divPos: ' + divPos);
      var divHeight = $(theID).height(); // get the height of the div in question
      console.log('divHeight: ' + divHeight);
      if (windowPos >= divPos && windowPos < (divPos + divHeight)) {
        $("a[href='" + theID + "']").addClass("nav-active");
      } else {
        $("a[href='" + theID + "']").removeClass("nav-active");
      }
    }
    
    if (windowPos + windowHeight == docHeight) {
      if (!$("navbar li:last-child a").hasClass("nav-active")) {
        var navActiveCurrent = $(".nav-active").attr("href");
        $("a[href='" + navActiveCurrent + "']").removeClass("nav-active");
        $("navbar li:last-child a").addClass("nav-active");
      }
    }
  });
});
